mapper.select:
/* getListConverterUploadLogTotalCount */
SELECT COUNT(converter_upload_log_id) 
FROM converter_upload_log
WHERE user_id = #{user_id}

	AND ${search_word} = #{search_value}


	AND ${search_word} LIKE '%' || #{search_value} || '%'



	AND insert_date >= TO_TIMESTAMP(#{start_date}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{end_date}, 'YYYYMMDDHH24MISSUS')

/* getListConverterUploadLog */
SELECT *
FROM converter_upload_log A
WHERE A.user_id = #{user_id}

	AND A.${search_word} = #{search_value}


	AND A.${search_word} LIKE '%' || #{search_value} || '%'



	AND A.insert_date >= TO_TIMESTAMP(#{start_date}, 'YYYYMMDDHH24MISSUS')




	AND A.insert_date <= TO_TIMESTAMP(#{end_date}, 'YYYYMMDDHH24MISSUS')




ORDER BY A.${order_word} ${order_value}


ORDER BY A.insert_date DESC, A.converter_upload_log_id DESC


OFFSET #{offset} LIMIT #{limit}

mapper.insert:
		/* insertFileInfo */

    		SELECT NEXTVAL('converter_upload_log_seq')

		INSERT INTO converter_upload_log(
			converter_upload_log_id, user_id, file_name, file_real_name, file_path, file_size, file_ext
		) values(
			#{converter_upload_log_id}, #{user_id}, #{file_name}, #{file_real_name}, #{file_path}, #{file_size}, #{file_ext}
		)

/* updateConverterCount */
UPDATE converter_upload_log
SET converter_count = converter_count + 1
WHERE converter_upload_log_id = #{converter_upload_log_id} AND user_id = #{user_id}
