mapper.select:
/* getObjectGroupByParentAndViewOrder */
SELECT * 
FROM object_group 
WHERE parent = #{parent} 


	AND view_order < #{view_order}

ORDER BY view_order DESC 



	AND view_order > #{view_order}

ORDER BY view_order 

LIMIT 1

mapper.insert:
/* insertObjectGroup */
INSERT INTO object_group(
	object_group_id, object_group_key, object_group_name, ancestor, parent, depth, view_order, use_yn, description
) values(
	#{object_group_id}, #{object_group_key}, #{object_group_name}, #{ancestor}, #{parent}, #{depth}, #{view_order}, #{use_yn}, #{description}
)

mapper.update:
/* updateObjectGroup */ UPDATE object_group SET group_key = #{group_key}, group_name = #{group_name},
ancestor = #{ancestor},
parent = #{parent},
depth = #{depth},
view_order = #{view_order},
use_yn = #{use_yn}, description = #{description} WHERE object_group_id = #{object_group_id}

UPDATE object_group
SET child_yn = #{child_yn}
WHERE object_group_id = #{object_group_id}
